\": [{\"@type\": \"WebPageElement\", \"isAccessibleForFree\": \"False\", \"cssSelector\": \".content-container\"}], \"video\": {\"@type\": \"VideoObject\", \"contentUrl\": \"https://streaming.hearstdigitalstudios.com/906664f5-0739-4d28-81aa-e3f519b521ad/video_rover_16x9_1080p_hd_1655488534_70378.mp4\", \"description\": \"Keto Beef Stew allows all the folks that have some dietary restrictions to enjoy the comfort of a nice bowl of beef stew. Don't forget its gluten free!\", \"duration\": \"PT1M41S\", \"embedUrl\": \"https://glimmer.hearstapps.com/amp.html?embedId=e6374fb5-e71a-40bb-aab0-7286129cecc9\", \"name\": \"No Flour? No Problem! This Keto Beef Stew Is Just As Good As The Classic\", \"thumbnailUrl\": \"https://hips.hearstapps.com/hmg-prod/images/12-seo-keto-beef-stew-12022-h-1582813705.jpg?crop=1.00xw:0.846xh;0,0.0841xh\", \"uploadDate\": \"2023-10-24T15:06:06.399905Z\"}, \"author\": {\"name\": \"Laura Rege\", \"url\": \"https://www.delish.com/author/224875/laura-rege/\", \"@type\": \"Person\", \"description\": \"<p>Laura Rege is a Food Stylist and Recipe Developer who regularly contributes recipes and styles videos and photos for Delish. If she isn't in the Delish kitchens, you'll most likely find her out on the town looking for the next food trends in the US and on her travels abroad.&nbsp;</p>\", \"jobTitle\": \"Recipe Developer\", \"image\": \"https://hips.hearstapps.com/rover/profile_photos/cc9183d9-718d-455c-ac4f-ab7c7714517f_1581914086.file\", \"sameAs\": []}, \"name\": \"Keto Beef Stroganoff\", \"prepTime\": \"PT10M\", \"cookTime\": \"PT0S\", \"totalTime\": \"PT10M\", \"recipeIngredient\": [\"3 tbsp. butter, divided\", \"1 lb. 80% lean ground beef\", \"Kosher salt\", \"Freshly ground black pepper\", \"1 1/2 lb. mushrooms, sliced&nbsp;\", \"1 garlic clove, minced\", \"1 c. low-sodium beef stock or bone broth\", \"4 oz. cream cheese\", \"1/2 c. sour cream\", \"2 tsp. Worcestershire sauce\", \"2 tsp. Dijon mustard\", \"1/2 tsp. paprika\", \"Finely chopped parsley, for garnish\", \"steamed&nbsp;cauliflower rice, for serving&nbsp;\"], \"recipeInstructions\": [{\"@type\": \"HowToStep\", \"text\": \"Heat a large skillet over medium-high and melt&nbsp;1 tablespoon of butter. Add ground beef and season with salt and pepper. Cook beef, stirring occasionally, until cooked through, about 10 minutes. Transfer meat to a plate.\"}, {\"@type\": \"HowToStep\", \"text\": \"Return skillet to medium-high heat and add remaining butter and mushrooms. Cook, tossing occasionally, until mushrooms are golden, about 7 minutes. Season with salt and pepper and add garlic. Cook until fragrant, 1 minute.&nbsp;Add broth, cream cheese, sour cream, Worcestershire, Dijon, and paprika to skillet. Bring to a simmer, stirring.&nbsp;\"}, {\"@type\": \"HowToStep\", \"text\": \"Return beef to skillet and stir to combine. Serve over cauliflower rice with garnish with parsley.\"}], \"recipeYield\": \"4 serving(s)\", \"recipeCuisine\": [\"American\"], \"recipeCategory\": [\"gluten-free\", \"low-carb\", \"low sugar\", \"nut-free\", \"weeknight meals\", \"main dish\"], \"description\": \"This keto beef stroganoff paired with steamed cauliflower rice is just as comforting as the original, but fit keto dieters. \", \"keywords\": \"American, gluten-free, low sugar, low-carb, main dish, nut-free, weeknight meals\", \"aggregateRating\": {\"@type\": \"AggregateRating\", \"ratingValue\": 4.3333335, \"reviewCount\": 12, \"worstRating\": 1, \"bestRating\": 5}, \"nutrition\": {\"@type\": \"NutritionInformation\", \"calories\": \" 572 Calories\", \"fatContent\": \"47 g\", \"saturatedFatContent\": \"23 g\", \"transFatContent\": \"2 g\", \"cholesterolContent\": \"149 mg\", \"sodiumContent\": \"1008 mg\", \"carbohydrateContent\": \"9 g\", \"fiberContent\": \"3 g\", \"sugarContent\": \"6 g\", \"proteinContent\": \"28 g\"}, \"@context\": \"http://schema.org\"}"}
